    private String CityText(String objName, String cityNameShort) {

        String cityName = null;
        try {

            JSONObject kvp = (new JSONObject(JSON_STRING)).getJSONObject(objName);
            cityName = kvp.getString(cityNameShort);

        } catch (Exception e) {
            e.printStackTrace();
        }

        return cityName;
    }

